mapper.update:
update know.answer
set answerTime = #{answerTime} and answerContent = #{answerContent}
where answerId = #{answerId};

mapper.select:
select * from know.answer where
answererId = #{answererId}
answerQuestionId = #{answerQuestionId}
order by
answerTime
answerLiked DESC
answerCollected DESC
answerTime DESC
;

mapper.update:
update know.answer
set answerCollected = answerCollected + #{answerCollected}
where answerId = #{answerId}

mapper.select:
select answererId
from know.answer
where answerId = #{answerId};
