mapper.update:
update favorites
set favoritesName = #{favoritesName}
where favoritesId = #{favoritesId};

update favorites
set contentNumber = contentNumber + #{contentNumber}, updateTime = #{updateTime}
where favoritesId = #{favoritesId};

mapper.delete:
delete from favoritescontent
where favoritesId = #{favoritesId}
and answerId = #{answerId};
